<script setup lang="ts">
import { useStore } from './store';

import "~/assets/css/main.css"
import 'material-symbols'
import data from './data.json';
import { computeData } from './data';

const confirmRoutes = data.slams.map(s => `/bestaetigung/${s.date}`);
prerenderRoutes(confirmRoutes);

const { nextSlam } = computeData();

useHead({
  link: [
    { rel: "icon", type: "image/png", href: "/favicon-96x96.png", sizes: "96x96" },
    { rel: "icon", type: "image/svg+xml", href: "/favicon.svg" },
    { rel: "shortcut icon", href: "/favicon.ico" },
    { rel: "apple-touch-icon", href: "/apple-touch-icon.png", sizes: "180x180" },
    { rel: "manifest", href: "/site.webmanifest" },
  ]
})

const store = useStore();

if (import.meta.client) {
  window.addEventListener('scroll', () => {
    store.value.scrolled = window.scrollY > 50;
  });
}

const navActive = ref(false);
const navFullyOpen = ref(false);

// watch(navActive, () => setTimeout(() => navFullyOpen.value = navActive.value, 500))

const route = useRoute();
const onHome = computed(() => route.name === 'index');
const darkenNav = computed(() => onHome.value && !store.value.scrolled && !navActive.value);

const routes = [
  ["/", "Startseite"],
  ["/veranstaltungen", "Veranstaltungen"],
  ["/mitmachen", "Mitmachen"],
  ["/kontakt", "Kontakt"],
]

const mobileNav = ref();
watch(navActive, () => {
  if (navActive.value) {
    mobileNav.value.style.marginTop = 0;
  } else {
    console.log(mobileNav.value.offsetHeight);
    mobileNav.value.style.marginTop = `-${mobileNav.value.offsetHeight}px`;
  }
});
const loading = ref(true);

const colors = computed(() => nextSlam.value?.colors);

watchEffect(() => {
  if (!loading.value && colors.value) {
    document.documentElement.style.setProperty("--color-theme-1", colors.value.background[0]);
    document.documentElement.style.setProperty("--color-theme-2", colors.value.background[1]);
    document.documentElement.style.setProperty("--color-theme-foreground", colors.value.foreground);

  }
})

onMounted(() => {
  console.log(mobileNav.value.style);
  mobileNav.value.style.marginTop = `-${mobileNav.value.offsetHeight}px`;
  console.log(mobileNav.value.style);
  setTimeout(() => {
    loading.value = false;
  }, 1)
});

const menuItemClasses = "transition-all duration-500 text-theme-foreground hover:opacity-80 outline-hover"
</script>

